import styled from 'styled-components';
import {SearchContainer, SearchInput} from './ListComponent';
import {messagesList} from '../mockData';


const Container = styled.div`
    display: flex;
    flex-direction: column;
    height: 100%;
    flex: 3;
    background: #f6f7f8;
`
const ChatHeader = styled.div `
    display: flex;
    flex-direction: row;
    background: #ededed;
    padding: 10px;
    align-items: center;
    gap: 10px;
`
const ProfileImg = styled.img`
    height: 32px;
    width: 32px;
    border-radius: 50%;
`;

const ChatBox = styled.div `
    display: flex;
    background: #f0f0f0;
    padding: 10px;
    align-items: center;
    bottom: 0px;
`;

const EmojiIcon = styled.img`
    height: 22px;
    width: 22px;
    opacity: 0.4;
    cursor: pointer;
`;

const MessageContainer = styled.div`
    display: flex;
    flex-direction: column;
    height: 100%;
    background: #e5ddd6;
    overflow-y: auto;
`

const MessageDiv = styled.div `
    justify-content: ${props => props.Received?  'flex-start' : 'flex-end'};
    display: flex;
    margin: 5px 15px;
`
const Message = styled.div `
    border-radius: 5px;
    background: ${props => props.Received?  'white' : '#daf8cb'};;
    max-width: 50%;
    color: #303030;
    padding: 8px 10px;
    font-size: 14px;
`

const ChatComponent = () => {
    return(
        <Container>
            <ChatHeader>
                <ProfileImg src='/Profile/Akhil.jpeg'/> Akhil Ch
            </ChatHeader>
            <MessageContainer>
                {messagesList.map(() => {
                    return (
                        <MessageDiv Received = {false}>
                            <Message Received = {false}>
                                Hi bro
                            </Message>
                        </MessageDiv>
                        )
                    })
                }
            </MessageContainer>
            <ChatBox>
                <SearchContainer>
                    <EmojiIcon src="/data.svg" />
                    <SearchInput placeholder = "Type a message" />
                </SearchContainer>
            </ChatBox>
        </Container>
    )
}

export default ChatComponent;
