import styled from 'styled-components'

const Container = styled.div`
    display: flex;
    flex-direction: column;
    height: 100%;
    flex: 3;
    background: #f6f7f8;
`
const ChatHeader = styled.div `
    display: flex;
    flex-direction: row;
    background: #ededed;
    padding: 10px;
    align-items: center;
    gap: 10px;
`
const ProfileImg = styled.img`
    height: 32px;
    width: 32px;
    border-radius: 50%;
`;

const ChatBox = styled.div `
    display: flex;
    background: #f0f0f0;
    padding: 10px;
    align-items: center;
    bottom: 0px;
`
const ChatComponent = () => {
    return(
        <Container>
            <ChatHeader>
                <ProfileImg src='/Profile/Akhil.jpeg'/> Akhil Ch
            </ChatHeader>
            <ChatBox>
                <SearchContainer>
                    <SearchInput placeholder = "Type a message" />
                </SearchContainer>
            </ChatBox>
        </Container>
    )
}

export default ChatComponent;
